package code;
import java.awt.Color;
class Template {
	int col,row,whiteRow,whiteCol;
	Color color;
	Template() {}
	Template(int col,int row,int whiteRow,int whiteCol,Color color) {
		// TODO Auto-generated constructor stub
		this.col = col;
		this.row = row;
		this.whiteRow = whiteRow;
		this.whiteCol = whiteCol;
		this.color = color;
	}
}
